package com.example.myapplication;

import android.content.pm.ApplicationInfo;
import android.content.pm.PackageManager;
import android.graphics.drawable.Drawable;
import android.os.Bundle;
import android.widget.ListView;

import androidx.activity.EdgeToEdge;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.graphics.Insets;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;

import java.io.File;
import java.util.ArrayList;
import java.util.List;

public class MainActivity extends AppCompatActivity {
    private ListView appListView;
    private AppListAdapter adapter;
    private List<AppInfo> appsList = new ArrayList<>();

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        EdgeToEdge.enable(this);
        setContentView(R.layout.activity_main);

        // Handle system bars insets
        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main), (v, insets) -> {
            Insets systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars());
            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom);
            return insets;
        });

        // Initialize ListView
        appListView = findViewById(R.id.app_list_view);
        loadInstalledApps();
    }

    private void loadInstalledApps() {
        PackageManager pm = getPackageManager();
        List<ApplicationInfo> packages = pm.getInstalledApplications(PackageManager.GET_META_DATA);

        for (ApplicationInfo packageInfo : packages) {
            // Skip system apps if needed
            if ((packageInfo.flags & ApplicationInfo.FLAG_SYSTEM) != 0) {
                continue;
            }

            String appName = packageInfo.loadLabel(pm).toString();
            Drawable icon = packageInfo.loadIcon(pm);
            boolean isSystemApp = (packageInfo.flags & ApplicationInfo.FLAG_SYSTEM) != 0;

            // Get app version
            String version;
            try {
                version = pm.getPackageInfo(packageInfo.packageName, 0).versionName;
            } catch (PackageManager.NameNotFoundException e) {
                version = "Unknown";
            }

            // Get app size
            long size = new File(packageInfo.sourceDir).length();

            AppInfo appInfo = new AppInfo(
                    packageInfo.packageName,
                    appName,
                    icon,
                    isSystemApp,
                    version,
                    size
            );
            appsList.add(appInfo);
        }

        // Sort apps alphabetically
        appsList.sort((a1, a2) -> a1.getAppName().compareToIgnoreCase(a2.getAppName()));

        // Set up adapter
        adapter = new AppListAdapter(this, appsList);
        appListView.setAdapter(adapter);
    }
}

package com.example.myapplication;

import android.content.Context;
import android.content.pm.PackageManager;
import android.util.Log;

import java.util.ArrayList;
import java.util.List;

public class PermissionUtils {
    public static List<String> getAppPermissions(Context context, String packageName) {
        List<String> permissions = new ArrayList<>();
        try {
            String[] requestedPermissions = context.getPackageManager()
                    .getPackageInfo(packageName, PackageManager.GET_PERMISSIONS)
                    .requestedPermissions;

            if (requestedPermissions != null) {
                for (String permission : requestedPermissions) {
                    if (isSpecialPermission(permission)) {
                        permissions.add(permission);
                    }
                }
            }
        } catch (PackageManager.NameNotFoundException e) {
            Log.e("PermissionUtils", "Error getting permissions", e);
        }
        return permissions;
    }

    private static boolean isSpecialPermission(String permission) {
        // List of special permissions to highlight
        String[] specialPermissions = {
                "android.permission.ACCESS_FINE_LOCATION",
                "android.permission.CAMERA",
                "android.permission.RECORD_AUDIO",
                "android.permission.READ_CONTACTS",
                "android.permission.WRITE_EXTERNAL_STORAGE"
        };

        for (String special : specialPermissions) {
            if (permission.equals(special)) {
                return true;
            }
        }
        return false;
    }
}

package com.example.myapplication;

import android.app.AlertDialog;
import android.content.Context;
import android.content.Intent;
import android.net.Uri;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ArrayAdapter;
import android.widget.ImageView;
import android.widget.TextView;
import android.widget.Toast;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;

import java.util.List;

public class AppListAdapter extends ArrayAdapter<AppInfo> {
    private Context context;
    private List<AppInfo> appsList;

    public AppListAdapter(@NonNull Context context, List<AppInfo> appsList) {
        super(context, R.layout.item_app_list, appsList);
        this.context = context;
        this.appsList = appsList;
    }

    @NonNull
    @Override
    public View getView(int position, @Nullable View convertView, @NonNull ViewGroup parent) {
        if (convertView == null) {
            convertView = LayoutInflater.from(context)
                    .inflate(R.layout.item_app_list, parent, false);
        }

        AppInfo currentApp = appsList.get(position);

        ImageView appIcon = convertView.findViewById(R.id.app_icon);
        TextView appName = convertView.findViewById(R.id.app_name);

        appIcon.setImageDrawable(currentApp.getIcon());
        appName.setText(currentApp.getAppName());

        convertView.setOnLongClickListener(v -> {
            showAppOptionsDialog(currentApp);
            return true;
        });

        return convertView;
    }

    private void showAppOptionsDialog(AppInfo appInfo) {
        String[] options = {
                "App Type",
                "Open App",
                "Uninstall",
                "View Details"
        };

        new AlertDialog.Builder(context)
                .setTitle(appInfo.getAppName())
                .setItems(options, (dialog, which) -> {
                    switch (which) {
                        case 0: // App Type
                            showAppTypeDialog(appInfo);
                            break;
                        case 1: // Open App
                            openApp(appInfo.getPackageName());
                            break;
                        case 2: // Uninstall
                            uninstallApp(appInfo.getPackageName());
                            break;
                        case 3: // View Details
                            showAppDetails(appInfo);
                            break;
                    }
                })
                .show();
    }

    private void showAppTypeDialog(AppInfo appInfo) {
        String type = appInfo.isSystemApp() ? "System App" : "User-Installed App";
        new AlertDialog.Builder(context)
                .setTitle("App Type")
                .setMessage(type)
                .setPositiveButton("OK", null)
                .show();
    }

    private void openApp(String packageName) {
        Intent launchIntent = context.getPackageManager()
                .getLaunchIntentForPackage(packageName);
        if (launchIntent != null) {
            context.startActivity(launchIntent);
        } else {
            Toast.makeText(context, "App cannot be launched", Toast.LENGTH_SHORT).show();
        }
    }

    private void uninstallApp(String packageName) {
        new AlertDialog.Builder(context)
                .setTitle("Uninstall App")
                .setMessage("Are you sure you want to uninstall this app?")
                .setPositiveButton("Uninstall", (dialog, which) -> {
                    Intent intent = new Intent(Intent.ACTION_DELETE);
                    intent.setData(Uri.parse("package:" + packageName));
                    context.startActivity(intent);
                })
                .setNegativeButton("Cancel", null)
                .show();
    }

    private void showAppDetails(AppInfo appInfo) {
        Intent intent = new Intent(context, AppDetailsActivity.class);
        intent.putExtra("PACKAGE_NAME", appInfo.getPackageName());
        context.startActivity(intent);
    }
}

package com.example.myapplication;

import android.graphics.drawable.Drawable;

public class AppInfo {
    private String packageName;
    private String appName;
    private Drawable icon;
    private boolean isSystemApp;
    private String version;
    private long size;

    public AppInfo(String packageName, String appName, Drawable icon,
                   boolean isSystemApp, String version, long size) {
        this.packageName = packageName;
        this.appName = appName;
        this.icon = icon;
        this.isSystemApp = isSystemApp;
        this.version = version;
        this.size = size;
    }

    // Getters and setters
    public String getPackageName() { return packageName; }
    public String getAppName() { return appName; }
    public Drawable getIcon() { return icon; }
    public boolean isSystemApp() { return isSystemApp; }
    public String getVersion() { return version; }
    public long getSize() { return size; }
}

package com.example.myapplication;

import android.content.pm.PackageInfo;
import android.content.pm.PackageManager;
import android.os.Bundle;
import android.widget.TextView;

import androidx.activity.EdgeToEdge;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.graphics.Insets;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;

import java.io.File;
import java.util.List;

public class AppDetailsActivity extends AppCompatActivity {
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        EdgeToEdge.enable(this);
        setContentView(R.layout.activity_app_details);

        // Handle system bars insets
        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.app_details_layout), (v, insets) -> {
            Insets systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars());
            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom);
            return insets;
        });

        // Get package name from intent
        String packageName = getIntent().getStringExtra("PACKAGE_NAME");

        // Populate details
        displayAppDetails(packageName);
    }

    private void displayAppDetails(String packageName) {
        PackageManager pm = getPackageManager();

        try {
            // Get package info
            PackageInfo packageInfo = pm.getPackageInfo(packageName,
                    PackageManager.GET_PERMISSIONS | PackageManager.GET_META_DATA);

            // Find views
            TextView nameView = findViewById(R.id.app_name_details);
            TextView versionView = findViewById(R.id.app_version);
            TextView sizeView = findViewById(R.id.app_size);
            TextView permissionsView = findViewById(R.id.app_permissions);

            // Set app name
            nameView.setText(packageInfo.applicationInfo.loadLabel(pm));

            // Set version
            versionView.setText("Version: " + packageInfo.versionName);

            // Calculate and set app size
            File apkFile = new File(packageInfo.applicationInfo.sourceDir);
            long size = apkFile.length();
            sizeView.setText("Size: " + formatFileSize(size));

            // Get and display permissions
            List<String> specialPermissions = PermissionUtils.getAppPermissions(this, packageName);
            if (!specialPermissions.isEmpty()) {
                StringBuilder permissionText = new StringBuilder("Special Permissions:
");
                for (String permission : specialPermissions) {
                    permissionText.append("â€¢ ").append(formatPermissionName(permission)).append("
");
                }
                permissionsView.setText(permissionText.toString());
            } else {
                permissionsView.setText("No special permissions found.");
            }

        } catch (PackageManager.NameNotFoundException e) {
            // Handle error
            TextView errorView = findViewById(R.id.app_name_details);
            errorView.setText("App details could not be loaded.");
        }
    }

    private String formatFileSize(long size) {
        if (size < 1024) return size + " B";
        int exp = (int) (Math.log(size) / Math.log(1024));
        char pre = "KMGTPE".charAt(exp-1);
        return String.format("%.1f %sB", size / Math.pow(1024, exp), pre);
    }

    private String formatPermissionName(String permission) {
        // Remove standard prefix and format nicely
        return permission.replace("android.permission.", "")
                .replace("_", " ")
                .toLowerCase();
    }
}
